Y = B + (r,0);
Z = D + r*dir(120);

draw(A--B--C--D--E--F--cycle);
draw(A--C--E--cycle);
draw(F--X--A,dashed);
draw(B--Y--C,dashed);
draw(D--Z--E,dashed);

